        console.log("yes")

        console.log("hello")
        await sendEmail(email, subjectValue, textValue);
    }
    return (
        <>
            <h1>Youssif Aymann</h1>
        <div className={"UserPanel"}>
            <div className={"flex"}>
            <img className={"pfp"} src={"https://picsum.photos/id/1/200/"}/>

            <hr className="line"/>
            </div>

            <div className={"actionPanel"}>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>

                </div>
                <div id={"emailContainer"}>
                    <p>Email: </p>
                    <p id={"email"} className={"hiddden"}>{email}</p>
                    <input type={"text"} placeholder={"subject"} id={"subject"}  value={subjectValue} onChange={handleInputChange}/>
                    <textarea id={"message"} placeholder={"message"}></textarea><br/>
                    <button onClick={sendMailHandler}> Send Email </button>
                </div>

                <hr className="line"/>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>
                </div>
                <hr className="line"/>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>
                </div>
                <hr className="line"/>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>
                </div>
                <br/>
            </div>
        </div>
        </>
    );
};

export default UserPanel;
