import React from 'react';
import mailLogo from '../images/mail.png'
import {useState,useEffect} from "react";
import certLogo from '../images/cert.png'
import foodLogo from '../images/food.png'
import updateSheetData from "../Utils/updateSheet";
import eyeLogo from '../images/eye.png'
import nerd from '../images/nerd.png'
import cool from '../images/cool.png'
import crazy from '../images/crazy.png'
import calm from '../images/nutreal.png'
import angry from '../images/angry.png'

const impressions ={
    nerd,
    cool,
    crazy,
    calm,
    angry
}

const UserPanel = ({panelProps, focusHander}) => {

   const [subjectValue, setSubjectValue] = useState('');

    const handleInputChange = (event) => {
        setSubjectValue(event.target.value);
    };

    const [textValue, setTextValue] = useState('');

    const handleTextChange = (event) => {
        setTextValue(event.target.value);
    };
    const [gradeValue, setGradeValue] = useState('');

    const handleGradeChange = (event) => {
        setGradeValue(event.target.value);

    };

    const [foodValue, setFoodValue] = useState('');

    const handleFoodChange = (event) => {
        setFoodValue(event.target.value);

    };

    const handleFoodSet = (event) => {
        updateSheetData(panelProps.index, {
            favouriteFood: foodValue
        })
    };

    const handleGradeSet = (event) => {
        updateSheetData(panelProps.index, {
            finalProjectGrade: gradeValue
        })
    };

    const [impValue, setImpValue] = useState('');

    const handleImpChange = (event) => {
        setImpValue(impressions[event.target.value]);
    };
    const postData = async() => {
        const url = 'http://localhost:3011/api/test-email/';
        const requestBody = {
            email: 'cruncogames@gmail.com',
            subject: 'hello',
            text: 'hi'
        };

        setTextValue("sex")

        const headers = {
            'Content-Type': 'application/json'
        };

        return fetch(url, {
            method: 'POST',
            headers: headers,
            body: JSON.stringify(requestBody)
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Request failed with status ' + response.status);
                }
                return response.json();
            })
            .then(data => {
                console.log('Response:', data);
            })
            .catch(error => {
                console.error('Error:', error);
            });
    };
    const sendMailHandler = async()=>{
        console.log("the")
        await postData();
    }


    useEffect(()=>{
        setImpValue(panelProps.impression.toLowerCase());
    }, [])


    return (
        <>
            <h1>{panelProps.name}</h1>
        <div className={"UserPanel"}>
            <div className={"flex"}>
            <img className={"pfp"} src={"https://picsum.photos/id/1/200/"}/>

            <hr className="line"/>
            </div>

            <div className={"actionPanel"}>
                <div className="action" id={"emailButton"}>
                    <img src={mailLogo}/>
                    <p>Send Email</p>
                </div>
                <div className={"actionContainer"} id={"emailContainer"}>
                    <p>Email: </p>
                    <p id={"email"} className={"hiddden"}>{panelProps.email}</p>
                    <input type={"text"} placeholder={"subject"} id={"subject"}  value={subjectValue} onChange={handleInputChange}/>
                    <textarea id={"message"} placeholder={"message"}  value={textValue} onChange={handleTextChange}></textarea><br/>
                    <button onClick={ postData}> Send Email </button>
                </div>

                <hr className="line"/>
                <div className="action" id={"certButton"}>
                    <img src={certLogo}/>
                    <p>Final Project Grade</p>
                </div>
                <div className={"actionContainer"} id={"certContainer"}>
                    <p>GRADE: </p>
                    <input type={"text"} id={"grade"} placeholder={panelProps.grade} value={gradeValue} onChange={handleGradeChange} />
                    <br/>
                    <button onClick={handleGradeSet}> Set Grade </button>
                </div>
                <hr className="line"/>
                <div id={"foodButton"} className="action">
                    <img src={foodLogo}/>
                    <p>Favourite Food</p>
                </div>
                <div className={"actionContainer"} id={"foodContainer"}>
                    <p>Food: </p>
                    <input type={"text"} id={"food"} placeholder={panelProps.food} value={foodValue} onChange={handleFoodChange} />
                    <br/>
                    <button onClick={handleFoodSet}> Set Favourite Food </button>
                </div>
                <hr className="line"/>
                <div id={"impButton"} className="action">
                    <img src={eyeLogo}/>
                    <p>Impression</p>
                </div>
                <div className={"actionContainer"} id={"impContainer"}>
                    <div className={"impressionView"}>
                        <p>Current:  </p>
                        <img src={impressions[impValue]}/>

                    </div>
<div className={"flex"}>
    {
        Object.keys(impressions).map(impression=> {
                return (
                    <button type={"text"} id={impression} value={impression} onClick={handleImpChange}>
                        <img src={impressions[impression]}/>
                    </button>)
            }
        )
    }
</div>



                </div>
                <hr className="line"/>
                <div className="action">
                    <p onClick={focusHander}>&lt; Back</p>
                </div>
                <br/>
            </div>
        </div>
        </>
    );
};

export default UserPanel;
