import React from 'react';
import mailLogo from '../images/mail.png'





async function sendEmail(email, subject, text) {
    const url = 'http://localhost:3010/api/test-email/';
    const requestBody = {
        email,
        subject,
        text
    };

    const headers = {
        'Content-Type': 'application/json'
    };

    try {
        const response = await fetch(url, {
            method: 'POST',
            headers: headers,
            body: JSON.stringify(requestBody)
        });

        if (!response.ok) {
            throw new Error('Request failed with status ' + response.status);
        }

        const data = await response.json();
        console.log('Response:', data);
    } catch (error) {
        console.error('Error:', error);
    }
}

async function sendMailHandlder(){
    console.log("yes")
    const emailElement = document.getElementById("email");
    const subjectElement = document.getElementById("subject");
    const textElement = document.getElementById("message");
    await sendEmail(emailElement.innerText, subjectElement.innerText, textElement.innerText);

    subjectElement.innerText="";
    textElement.innerText="";
}


const UserPanel = ({ name, email, grade, reaction }) => {
    return (
        <>
            <h1>Youssif Ayman</h1>
        <div className={"UserPanel"}>
            <div className={"flex"}>
            <img className={"pfp"} src={"https://picsum.photos/id/1/200/"}/>

            <hr className="line"/>
            </div>

            <div className={"actionPanel"}>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send the Email</p>

                </div>
                <div id={"emailContainer"}>
                    <p>Email: </p>
                    <p id={"email"} className={"hiddden"}>{email}</p>
                    <input type={"text"} placeholder={"subject"} id={"subject"}/>
                    <textarea id={"message"} placeholder={"message"}></textarea><br/>
                    <button onClick={sendMailHandlder}> Send Email </button>
                </div>

                <hr className="line"/>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>
                </div>
                <hr className="line"/>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>
                </div>
                <hr className="line"/>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>
                </div>
                <br/>
            </div>
        </div>
        </>
    );
};

export default UserPanel;
