import React from 'react';
import mailLogo from '../images/mail.png'

import sgMail from '@sendgrid/mail';

sgMail.setApiKey('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');

const sendEmail = async (email, subject, message) => {
    const msg = {
        to: email,
        from: 'youssifayman2004@gmail.com',
        subject,
        html: `<strong>${message}</strong>`,
    };

    try {
        await sgMail.send(msg);
        console.log('Email sent');
    } catch (error) {
        console.error(error);
    }
};

// Call the function
sendEmail('recipient@example.com', 'Test Subject', 'Hello, world!');

const UserPanel = ({ name, email, grade, reaction }) => {
    return (
        <>
            <h1>Youssif Ayman</h1>
        <div className={"UserPanel"}>
            <div className={"flex"}>
            <img className={"pfp"} src={"https://picsum.photos/id/1/200/"}/>

            <hr className="line"/>
            </div>

            <div className={"actionPanel"}>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>

                </div>
                <div id={"emailContainer"}>
                    <p>Email: </p>
                    <input type={"text"} placeholder={"subject"} id={"subject"}/>
                    <textarea id={"message"} placeholder={"message"}></textarea><br/>
                    <button> Send Email </button>
                </div>

                <hr className="line"/>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>
                </div>
                <hr className="line"/>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>
                </div>
                <hr className="line"/>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>
                </div>
                <br/>
            </div>
        </div>
        </>
    );
};

export default UserPanel;
