import React from 'react';
import mailLogo from '../images/mail.png'
import {useState} from "react";




const UserPanel = ({ name, email, grade, reaction }) => {

    const [subjectValue, setSubjectValue] = useState('');

    const handleInputChange = (event) => {
        setSubjectValue(event.target.value);
    };

    const [textValue, setTextValue] = useState('');

    const handleTextChange = (event) => {
        setTextValue(event.target.value);
    };

    const sendEmail = async (email, subject, text)=> {

        console.log("yes")
        const url = 'http://localhost:3011/api/test-email/';
        const requestBody = {
            email,
            subject,
            text
        };
        setTextValue(requestBody)

        const headers = {
            'Content-Type': 'application/json'
        };

        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: headers,
                body: JSON.stringify(requestBody)


            });
            setTextValue("")
            setSubjectValue("")

            if (!response.ok) {
                throw new Error('Request failed with status ' + response.status);
            }

            setTextValue("")
            setSubjectValue("")

            const data = await response.json();
            console.log('Response:', data);
        } catch (error) {
            console.error('Error:', error);
        }
    }

    const sendMailHandler = async () =>{
        const done = await sendEmail(email, subjectValue, textValue);

    }
    return (
        <>
            <h1>Youssif Aymann</h1>
        <div className={"UserPanel"}>
            <div className={"flex"}>
            <img className={"pfp"} src={"https://picsum.photos/id/1/200/"}/>

            <hr className="line"/>
            </div>

            <div className={"actionPanel"}>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>

                </div>
                <div id={"emailContainer"}>
                    <p>Email: </p>
                    <p id={"email"} className={"hiddden"}>{email}</p>
                    <input type={"text"} placeholder={"subject"} id={"subject"}  value={subjectValue} onChange={handleInputChange}/>
                    <textarea id={"message"} placeholder={"message"}  value={textValue} onChange={handleTextChange}></textarea><br/>
                    <button onClick={sendMailHandler}> Send Email </button>

                </div>

                <hr className="line"/>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>
                </div>
                <hr className="line"/>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>
                </div>
                <hr className="line"/>
                <div className="action">
                    <img src={mailLogo}/>
                    <p>Send Email</p>
                </div>
                <br/>
            </div>
        </div>
        </>
    );
};

export default UserPanel;
